import gmpy2

# Quadratic Residues

def checkQuadraticResidues(p, x):
    for a in range(2, p):
        if pow(a, 2, p) == x:
            return a, True
    return 0, False

ints = [14, 6, 11]
tmp = []
for i in ints:
    tmp.append(checkQuadraticResidues(29, i))

print(tmp)

