# Chinese Remainder Theorem

a = [2, 3, 5]
n = [5, 11, 17]
N = 1
for i in n:
    N *= i

Ni = [N // n[i] for i in range(3)]

M = [int(gmpy2.invert(Ni[i], n[i])) for i in range(3)]

S = 0
for i in range(3):
    S += a[i] * Ni[i] * M[i]
S = S % N
print(S)
